import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "../CustomButtons/Button.jsx";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Grid from "@material-ui/core/Grid";
import axios from "axios";
import { connect } from "react-redux";
import updateCarrierName from "../../actions/updateCarrierName";
import BusinessDetails from "./BusinessDetails.js";
import FormControl from "@material-ui/core/FormControl";
const styles = theme => ({
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5",
    width: 800
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 800,
    height: 75
  },
  nextButton: {
    backgroundColor: "#1C80B5"
  },
  card: {
    height: 400
  }
});
class CarrierDetails extends React.Component {
  state = {
    LocalcarrierName: ""
  };

  constructor(props) {
    super(props);
  }

  handleChange = LocalcarrierName => event => {
    this.setState({ [LocalcarrierName]: event.target.value });
  };
  handleState = () => {
    this.props.handleState(this.state.LocalcarrierName);
  };
  handleSubmit = e => {
    e.preventDefault();
    console.log(this.state);
    this.handleState();
    axios
      .post(`http://10.24.46.14:8080/api/carriers`, this.state)
      .then(function(response) {
        console.log(response);
      });
    this.props.history.push(`/admin/BusinessDetails`);
  };
  render() {
    const { classes } = this.props;

    return (
      <form onSubmit={this.handleSubmit}>
        <Grid
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "70vh"
          }}
        >
          <Card>
            <CardHeader
              classes={{ title: classes.label }}
              title="Name of the Carrier"
            />
            <Divider variant="middle" />
            <CardContent>
              <FormControl
                formControlProps={{
                  fullWidth: true,
                  variant: "filled",
                  required: true,
                  error: true
                }}
              >
                <TextField
                  required
                  id="carrier-name"
                  variant="standard"
                  label="Required"
                  className={classes.textField}
                  value={this.state.LocalcarrierName}
                  onChange={this.handleChange("LocalcarrierName")}
                  margin="normal"
                />
              </FormControl>
            </CardContent>
            <Grid
              container
              alignItems="flex-start"
              justify="flex-end"
              direction="row"
            >
              <CardContent>
                <Button type="submit" className={classes.nextButton}>
                  Next
                </Button>
              </CardContent>
            </Grid>
          </Card>
        </Grid>
      </form>
    );
  }
}
const mapStateToProps = state => ({
  carrierName: state.carrierName
});
const mapActionToProps = {
  handleState: updateCarrierName
};
const ReduxCarrierDetails = connect(
  mapStateToProps,
  mapActionToProps
)(CarrierDetails);
export default withStyles(styles)(ReduxCarrierDetails);
