  };
  validateSelections = () => {
    const { legalentity, states, product, designer } = this.state;
    return !legalentity || !states || !product || !designer;
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  deletepanels = event => {
    const { panels } = this.state;
    var index = event.currentTarget.attributes.deletevalue.nodeValue;
    console.log(event.currentTarget.attributes.deletevalue.nodeValue);
    panels.splice(index, 1);
    this.setState({ panels });
  };
  render() {
    const { classes } = this.props;
    const { legalentity, states, product, designer, panels } = this.state;
    return (
      <div className={classes.root}>
        <Card className={classes.addresscard}>
          <CardHeader
            classes={{ title: classes.label }}
            title="Configure Legal Entity and Prod Type"
          />
          <Divider className={classes.divider} />
          <CardContent>
            <Grid container spacing={24} className={classes.label}>
              <Grid item xs={3}>
                <TextField
                  id="Legal_Entity"
                  select
                  className={classes.textField}
                  label="Legal Entity"
                  value={this.state.legalentity}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleLegalEntity("legalentity")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {legal_entity_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="State_Value"
                  select
                  className={classes.textField}
                  label="State"
                  value={this.state.states}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleStates("states")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {states_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={3}>
                <TextField
                  id="Product"
                  select
                  className={classes.textField}
                  label="Product"
                  value={this.state.product}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleProduct("product")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {product_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="Designer"
                  select
                  className={classes.textField}
                  label="Product_Type"
                  value={this.state.designer}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleProductType("designer")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {designer_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={1}>
                <Fab
                  color="primary"
                  disabled={this.validateSelections()}
                  onClick={this.addNewlist}
                  aria-label="Add"
                  className={classes.fab}
                >
                  <AddIcon />
                </Fab>
              </Grid>
              <Grid item xs={11}>
                {panels.map((event, index) => (
                  <div key={index}>
                    <Paper className={classes.paper}>
                      {`${event.legalentity} in ${event.states} sells ${
                        event.product
                      } and configured as Prod_Type ${event.designer}`}
                      <Fab
                        color="primary"
                        deletevalue={index}
                        onClick={this.deletepanels}
                        size="small"
                        aria-label="Delete"
                        className={classes.deletefab}
                      >
                        <DeleteIcon />
                      </Fab>
                    </Paper>
                  </div>
                ))}
              </Grid>
            </Grid>
          </CardContent>
          <CardActions>
            <Button className={classes.button} onClick={this.handleNext}>
              next
            </Button>
          </CardActions>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const ReduxLegalEntity2 = connect(mapStateToProps)(LegalEntity2);
export default withStyles(styles)(ReduxLegalEntity2);
