import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import Typography from "@material-ui/core/Typography";
import MenuItem from "@material-ui/core/MenuItem";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import BankDetails from "./BankDetails";
import Button from "@material-ui/core/Button";
import AddressDetails from "./AddressDetails";

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  button: {
    position: "absolute",
    bottom: "20rem",
    right: "2rem",
    justifyContent: "center",
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 125,
    //height: 50,
    fontSize: "14px",
    color: "#ffffff"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  card: {
    height: 200,
    width: 800
  },
  addresscard: {
    height: 800,
    width: 800
  },

  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  }
});

class LegalEntity extends React.Component {
  state = {
    nameoflegalentity: "",
    codeoflegalentity: "",
    address: [],
    bank: ""
  };

  constructor(props) {
    super(props);
  }
  handleNext = () => {
    const { panels } = this.state;
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      nameoflegalentity: this.state.nameoflegalentity,
      codeoflegalentity: this.state.codeoflegalentity,
      address: this.state.address,
      bank: this.state.bank
    };
    console.log(JSON.stringify(post));
    fetch(`http://10.24.46.158:8080/legalEntityapi/createLegalEntity`, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(data => console.log(data));
    this.props.history.push(`/admin/LegalEntity2`);
    //console.log({ panels });
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };

  // handleAddress = () =>{
  //   if(this.state.isaddress)
  //   {
  //   const address=this.state.address;
  //   address.push({id:2});
  //   this.setState(address);
  //   this.setState({isaddress:false});
  // }
  // }
  handleAddress = value => {
    const { address } = this.state;
    address.push({ value });
    this.setState({ address });
    // console.log(this.state.address);
  };
  handlebank = value => {
    this.setState({ bank: value });
  };
  render() {
    const { classes } = this.props;
    console.log(this.state.Legalentity);
    return (
      <div className={classes.root}>
        <Grid
          container
          spacing={24}
          direction="column"
          alignItems="center"
          justify="center"
          className={classes.label}
          style={{ minHeight: "100vh" }}
        >
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Name of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.nameoflegalentity}
                  onChange={this.handleChange("nameoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Code of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.codeoflegalentity}
                  onChange={this.handleChange("codeoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            {/* {this.state.address.map(card => (
          <AddressDetails status={this.state.isaddress} event={this.handleAddress} key={card.id} />
        ))}    */}
            <AddressDetails onAddressChange={this.handleAddress} />
          </Grid>
          <Grid item xs={12}>
            <BankDetails event={this.handlebank} />
          </Grid>
        </Grid>
        <Button className={classes.button} onClick={this.handleNext}>
          next
        </Button>
      </div>
    );
  }
}
export default withStyles(styles)(LegalEntity);
