import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import AddressDetails from "./AddressDetails";
import { connect } from "react-redux";
import axios from "axios";
const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  button: {
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 150,
    height: 50,
    fontSize: "14px",
    color: "#ffffff",
    justifyContent: "center"
  },
  LEbutton: {
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 250,
    height: 50,
    fontSize: "14px",
    color: "#ffffff",
    justifyContent: "center"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  card: {
    height: 200,
    width: 800
  },
  bankCard: {
    height: 400,
    width: 800
  },
  addresscard: {
    height: 800,
    width: 800
  },

  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  }
});
const mobile_types = [
  {
    value: "Mobile",
    label: "Mobile"
  },
  {
    value: "Office",
    label: "Office"
  }
];
class LegalEntity extends React.Component {
  state = {
    nameoflegalentity: "",
    codeoflegalentity: "",
    address: "",
    bank: "",
    accountnumber: "",
    bankAccountInfo: [],
    showmobile: false,
    addresstype: "",
    addressline1: "",
    addressline2: "",
    city: "",
    states: "",
    mtype1: "Mobile",
    mobilenumber1: "",
    mtype2: "Office",
    mobilenumber2: "",
    zip: "",
    email: "",
    website: "",
    addressTypeOption: [],
    stateCodeOption: []
  };

  constructor(props) {
    super(props);
  }
  componentDidMount() {
    axios
      .get(`http://10.24.46.202:9091/configurator/getBankInfo/${10}`)
      .then(res => {
        const bankAccountInfo = res.data;
        console.log(bankAccountInfo);
        this.setState({ bankAccountInfo });
      });
    axios
      .get(`http://10.24.46.202:9091/configurator/getStateCode`)
      .then(res => {
        const stateCodeOption = res.data;
        console.log(stateCodeOption);
        this.setState({ stateCodeOption });
      });
    axios
      .get(`http://10.24.46.202:9091/configurator/getAddressType`)
      .then(res => {
        const addressTypeOption = res.data;
        console.log(addressTypeOption);
        this.setState({ addressTypeOption });
      });
  }
  handlemobilechange = () => {
    const { showmobile } = this.state;
    this.setState({ showmobile: !showmobile });
  };
  handleaddress = () => {
    //  e.preventDefault();
    this.props.onAddressChange(this.state);
    this.setState({ addresstype: "" });
    this.setState({ addressline1: "" });

    this.setState({ addressline2: "" });
    this.setState({ city: "" });
    this.setState({ states: "" });
    this.setState({ mobilenumber1: "" });
    this.setState({ mobilenumber2: "" });
    this.setState({ zip: "" });
    this.setState({ email: "" });
    this.setState({ website: "" });
  };

  handlemobilevalue = (type, value) => {
    this.setState({ [type]: value });
    // const{mobile}=this.state;
    // mobile.push(value);
    // this.setState({mobile});
  };

  handleaddlegal = e => {
    e.preventDefault();
    const { panels } = this.state;
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      nameoflegalentity: this.state.nameoflegalentity,
      codeoflegalentity: this.state.codeoflegalentity,
      address: this.state.address,
      bank: this.state.bank
    };
    console.log(JSON.stringify(post));
    fetch(`http://10.24.46.59:8080/legalEntityapi/createLegalEntityPart1`, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(data => console.log(data));
    this.setState({ nameoflegalentity: "" });
    this.setState({ codeoflegalentity: "" });
  };
  handleNext = () => {
    this.props.history.push(`/admin/LegalEntity2`);
    //console.log({ panels });
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  handlebank = e => {
    e.preventDefault();
    this.props.event(this.state);
    this.setState({ accountnumber: "" });
  };
  render() {
    const { classes } = this.props;
    console.log(this.state.Legalentity);
    return (
      <form className={classes.root} onSubmit={this.handleaddlegal}>
        <Grid
          container
          spacing={24}
          direction="column"
          alignItems="center"
          justify="center"
          className={classes.label}
          style={{ minHeight: "100vh" }}
        >
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Name of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.nameoflegalentity}
                  onChange={this.handleChange("nameoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Code of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.codeoflegalentity}
                  onChange={this.handleChange("codeoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.bankCard}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Bank Details"
              />
              <Divider className={classes.divider} />
              <CardContent>
                <Grid container spacing={24} className={classes.label}>
                  <Grid item xs={11}>
                    <TextField
                      select
                      id="Account Number"
                      variant="outlined"
                      label="Account Number"
                      className={classes.textField}
                      fullWidth
                      value={this.state.accountnumber}
                      onChange={this.handleChange("accountnumber")}
                      margin="normal"
                    >
                      {this.state.bankAccountInfo.map((bankAccount, index) => (
                        <MenuItem key={index} value={bankAccount}>
                          {bankAccount}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.addresscard}>
              <CardHeader classes={{ title: classes.label }} title="Address" />
              <Divider className={classes.divider} />
              <CardContent>
                <Grid container spacing={24} className={classes.label}>
                  <Grid item xs={4}>
                    <TextField
                      select
                      id="addresstype"
                      variant="outlined"
                      label="Address Type"
                      className={classes.textField}
                      fullWidth
                      value={this.state.addresstype}
                      onChange={this.handleChange("addresstype")}
                      margin="normal"
                    >
                      {this.state.addressTypeOption.map(
                        (addressTypeOption, index) => (
                          <MenuItem key={index} value={addressTypeOption}>
                            {addressTypeOption}
                          </MenuItem>
                        )
                      )}
                    </TextField>
                  </Grid>
                  <Grid item xs={7}>
                    <TextField
                      id="Addressline1-name"
                      variant="outlined"
                      placeholder="Address Line 1"
                      className={classes.textField}
                      fullWidth
                      value={this.state.addressline1}
                      onChange={this.handleChange("addressline1")}
                      margin="normal"
                    />
                  </Grid>
                  <Grid item xs={11}>
                    <TextField
                      id="Addressline2-name"
                      variant="outlined"
                      placeholder="Address Line 2"
                      className={classes.textField}
                      value={this.state.addressline2}
                      fullWidth
                      onChange={this.handleChange("addressline2")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid item xs={4}>
                    <TextField
                      id="City"
                      variant="outlined"
                      placeholder="City"
                      className={classes.textField}
                      value={this.state.city}
                      fullWidth
                      onChange={this.handleChange("city")}
                      margin="normal"
                    />
                  </Grid>

                  <Grid item xs={4}>
                    <TextField
                      select
                      id="State"
                      variant="outlined"
                      label="State"
                      className={classes.textField}
                      fullWidth
                      value={this.state.states}
                      onChange={this.handleChange("states")}
                      margin="normal"
                    >
                      {this.state.stateCodeOption.map(
                        (stateCodeOption, index) => (
                          <MenuItem key={index} value={stateCodeOption}>
                            {stateCodeOption}
                          </MenuItem>
                        )
                      )}
                    </TextField>
                  </Grid>
                  <Grid item xs={3}>
                    <TextField
                      id="ZIP"
                      variant="outlined"
                      placeholder="ZIP"
                      type="Number"
                      fullWidth
                      className={classes.textField}
                      value={this.state.zip}
                      onChange={this.handleChange("zip")}
                      margin="normal"
                    />
                  </Grid>
                  <Grid item xs={3} className={classes.mtype}>
                    <TextField
                      id="standard-select-contacttype"
                      select
                      className={classes.textField}
                      value={this.state.mtype1}
                      variant="outlined"
                      fullWidth
                      onChange={this.handleChange("mtype1")}
                      SelectProps={{
                        MenuProps: {
                          className: classes.menu
                        }
                      }}
                    >
                      {mobile_types.map(option => (
                        <MenuItem key={option.value} value={option.value}>
                          {option.label}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>
                  <Grid item xs={8} className={classes.mobilenumber}>
                    <TextField
                      id="mobilenumber"
                      variant="outlined"
                      placeholder="Mobile Number"
                      fullWidth
                      className={classes.textField}
                      value={this.state.mobilenumber1}
                      onChange={this.handleChange("mobilenumber1")}
                      margin="normal"
                    />
                  </Grid>
                  <Grid item xs={3} className={classes.mtype}>
                    <TextField
                      id="standard-select-contacttype"
                      select
                      className={classes.textField}
                      value={this.state.mtype2}
                      variant="outlined"
                      fullWidth
                      onChange={this.handleChange("mtype2")}
                      SelectProps={{
                        MenuProps: {
                          className: classes.menu
                        }
                      }}
                    >
                      {mobile_types.map(option => (
                        <MenuItem key={option.value} value={option.value}>
                          {option.label}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>
                  <Grid item xs={8} className={classes.mobilenumber}>
                    <TextField
                      id="mobilenumber"
                      variant="outlined"
                      placeholder="Mobile Number"
                      fullWidth
                      className={classes.textField}
                      value={this.state.mobilenumber2}
                      onChange={this.handleChange("mobilenumber2")}
                      margin="normal"
                    />
                  </Grid>
                  {/* {showmobile && <Mobile/>} */}
                  {/* </Grid>    */}

                  <Grid item xs={7}>
                    <TextField
                      id="Email"
                      variant="outlined"
                      placeholder="Email"
                      type="Email"
                      fullWidth
                      className={classes.textField}
                      value={this.state.email}
                      onChange={this.handleChange("email")}
                      margin="normal"
                    />
                  </Grid>
                  <Grid item xs={4}>
                    <TextField
                      id="Website"
                      variant="outlined"
                      placeholder="Website"
                      fullWidth
                      className={classes.textField}
                      value={this.state.website}
                      onChange={this.handleChange("website")}
                      margin="normal"
                    />
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Grid>

          <CardActions>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              disabled={this.validateLegalEntity()}
              className={classes.LEbutton}
            >
              Add Legal Entity
            </Button>
            <Button
              variant="contained"
              color="primary"
              disabled={this.validateNext()}
              className={classes.button}
              onClick={this.handleNext}
            >
              next
            </Button>
          </CardActions>
        </Grid>
      </form>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const ReduxLegalEntity = connect(mapStateToProps)(LegalEntity);
export default withStyles(styles)(ReduxLegalEntity);
