import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center"
  },
  button: {
    margin: theme.spacing.unit,
    textTransform: "none",
    fontSize: 18,
    padding: "6px 12px",
    backgroundColor: "#1C80B5",
    justifyContent: "center"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  addresscard: {
    height: 800,
    width: 800
  },

  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  }
});

class BankDetails extends React.Component {
  state = {
    accountnumber: "",
    banknumcode: "",
    batchconame: "",
    sitecode: "",
    batchdescription: "",
    batchinfo: "",
    batchgroupcode: "",
    bankname: "",
    merchantID: "",
    securitykey: ""
  };

  constructor(props) {
    super(props);
  }

  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };

  handlebank = () => {
    this.props.event(this.state);
  };

  render() {
    const { classes } = this.props;

    return (
      <div
        className={classes.root}
        // style={{
        //   display: "flex",
        //  justifyContent: "center",
        // alignItems: "center",
        //   height: "100vh"

        // }}
      >
        <Card className={classes.addresscard}>
          <CardHeader classes={{ title: classes.label }} title="Bank Details" />
          <Divider className={classes.divider} />
          <CardContent>
            <Grid container spacing={24} className={classes.label}>
              <Grid item xs={7}>
                <TextField
                  id="Account Number"
                  variant="outlined"
                  placeholder="Account Number"
                  className={classes.textField}
                  fullWidth
                  value={this.state.accountnumber}
                  onChange={this.handleChange("accountnumber")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={4}>
                <TextField
                  id="Bank Num Code"
                  variant="outlined"
                  placeholder="Bank Num Code"
                  className={classes.textField}
                  fullWidth
                  value={this.state.banknumcode}
                  onChange={this.handleChange("banknumcode")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={7}>
                <TextField
                  id="Batch Co Name"
                  variant="outlined"
                  placeholder="Batch Co Name"
                  className={classes.textField}
                  value={this.state.batchconame}
                  fullWidth
                  onChange={this.handleChange("batchconame")}
                  margin="normal"
                />
              </Grid>

              <Grid item xs={4}>
                <TextField
                  id="Site Code"
                  variant="outlined"
                  placeholder="Site Code"
                  className={classes.textField}
                  value={this.state.sitecode}
                  fullWidth
                  onChange={this.handleChange("sitecode")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={11}>
                <TextField
                  id="Batch Description"
                  variant="outlined"
                  placeholder="Batch Description"
                  className={classes.textField}
                  fullWidth
                  value={this.state.batchdescription}
                  onChange={this.handleChange("batchdescription")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={8}>
                <TextField
                  id="Additional Batch Info"
                  variant="outlined"
                  placeholder="Additional Batch Info"
                  fullWidth
                  className={classes.textField}
                  value={this.state.batchinfo}
                  onChange={this.handleChange("batchinfo")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={3}>
                <TextField
                  id="Batch Group Code"
                  variant="outlined"
                  placeholder="Batch Group Code"
                  fullWidth
                  className={classes.textField}
                  value={this.state.batchgroupcode}
                  onChange={this.handleChange("batchgroupcode")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={4}>
                <TextField
                  id="Bank Name"
                  variant="outlined"
                  placeholder="Bank Name"
                  fullWidth
                  className={classes.textField}
                  value={this.state.bankname}
                  onChange={this.handleChange("bankname")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={4}>
                <TextField
                  id="Merchant ID"
                  variant="outlined"
                  placeholder="Merchant ID"
                  fullWidth
                  className={classes.textField}
                  value={this.state.merchantID}
                  onChange={this.handleChange("merchantID")}
                  margin="normal"
                />
              </Grid>
              <Grid item xs={3}>
                <TextField
                  id="Security Key"
                  variant="outlined"
                  placeholder="Security Key"
                  fullWidth
                  className={classes.textField}
                  value={this.state.securitykey}
                  onChange={this.handleChange("securitykey")}
                  margin="normal"
                />
              </Grid>
            </Grid>
          </CardContent>
          <CardActions style={{ justifyContent: "center" }}>
            <Grid item xs={11}>
              <Button
                fullWidth
                variant="contained"
                color="primary"
                className={classes.button}
                onClick={this.handlebank}
              >
                Add Bank Details
              </Button>
            </Grid>
          </CardActions>
        </Card>
      </div>
    );
  }
}

export default withStyles(styles)(BankDetails);
