import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import FormLabel from "@material-ui/core/FormLabel";
import Fade from "@material-ui/core/Fade";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import MuiExpansionPanel from "@material-ui/core/ExpansionPanel";
import MuiExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import MuiExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";
import updateCarrierCode from "../../actions/udpdateCarrierCode";
import { connect } from "react-redux";
import updateOffExchange from "../../actions/updateOfExchange";
import updateOnExchange from "../../actions/updateOnExchange";
import updateGroup from "../../actions/updateGroup";
import axios from "axios";
const ExpansionPanelSummary = withStyles({
  root: {
    backgroundColor: "rgba(0,0,0,.03)",
    borderBottom: "1px solid rgba(0,0,0,.125)"
  }
})(props => <MuiExpansionPanelSummary {...props} />);

ExpansionPanelSummary.muiName = "ExpansionPanelSummary";

const ExpansionPanelDetails = withStyles(theme => ({
  root: {
    padding: theme.spacing.unit
  }
}))(MuiExpansionPanelDetails);

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex"
    //justifyContent: "center"
    //    alignItems: "center",
    //         height: "100vh"
  },
  HPSBlue: {
    backgroundColor: "#1C80B5",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#FFFFF"
  },
  default: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  nextButton: {
    backgroundColor: "#1C80B5",
    width: 150,
    height: 40,
    fontSize: "14px",
    position: "absolute",
    bottom: "2rem",
    right: "2rem",
    color: "#ffffff"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "30px",
    textAlign: "center",
    margin: theme.spacing.unit * 2
  },
  clearButton: {
    margin: theme.spacing.unit * 2,
    width: 150,
    height: 40,
    fontSize: "14px",
    //float: "left"
    position: "absolute",
    bottom: "2rem",
    left: "2rem"
  }
});

class ContainedButtons extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      individual: false,
      onExchange: false,
      offExchange: false,
      group: false,
      individualCarrierCode: "",
      groupCarrierCode: "",
      expanded: "panel1"
    };
  }
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  handleUpdateCarrierCode = () => {
    this.props.handleUpdateCarrierCode(this.state.individualCarrierCode);
  };
  handleUpdateOffExchange = () => {
    this.props.handleUpdateOffExchange(this.state.offExchange);
  };

  handleUpdateOnExchange = () => {
    this.props.handleUpdateOnExchange(this.state.onExchange);
  };
  handleUpdateGroup = () => {
    this.props.handleUpdateGroup(this.state.group);
  };
  handleSubmit = e => {
    e.preventDefault();
    const post = {
      carrierName: this.props.carrierName,
      onExchange: this.state.onExchange,
      offExchange: this.state.offExchange,
      individualCarrierCode: this.state.individualCarrierCode,
      groupCarrierCode: this.state.groupCarrierCode,
      group: this.state.group
    };
    console.log(JSON.stringify(post));
    this.handleUpdateCarrierCode();
    this.handleUpdateOffExchange();
    this.handleUpdateOnExchange();
    this.handleUpdateGroup();
    fetch(`http://10.24.46.59:8080/businessapi/createBusinessType`, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(data => console.log(data));
    if (this.state.offExchange === true)
      this.props.history.push(`/admin/StatesOffExchange`);
    else if (this.state.onExchange === true)
      this.props.history.push(`/admin/StatesOnExchange`);
    else if (this.state.group === true)
      this.props.history.push(`/admin/StatesGroup`);
    else alert("Please choose your business type !!");
  };
  changeIndividual = () => {
    if (this.state.individual === true) {
      this.setState({ individual: false });
      this.setState({ onExchange: false });
      this.setState({ offExchange: false });
    } else this.setState({ individual: true });
  };
  changeOnExchange = () => {
    if (this.state.onExchange === true) this.setState({ onExchange: false });
    else {
      this.setState({ onExchange: true });
    }
  };
  changeOffExchange = () => {
    if (this.state.offExchange === true) this.setState({ offExchange: false });
    else {
      this.setState({ offExchange: true });
    }
  };
  changeGroup = () => {
    if (this.state.group === true) this.setState({ group: false });
    else {
      this.setState({ group: true });
    }
  };
  clear = () => {
    axios
      .get(
        `http://localhost:8080/businessapi/deleteBusinessType/${
          this.props.carrierName
        }`
      )
      .then(res => res.json())
      .then(data => console.log(data));
  };
  render() {
    const { classes } = this.props;
    return (
      <form onSubmit={this.handleSubmit}>
        <FormLabel className={classes.label} component="legend">
          Please Choose your Business Type
        </FormLabel>

        <Grid
          container
          spacing={2}
          direction="column"
          alignItems="center"
          justify="center"
          style={{ minHeight: "70vh" }}
        >
          <MuiExpansionPanel>
            <ExpansionPanelSummary onClick={this.changeIndividual}>
              <Button
                variant="outlined"
                onClick={this.changeIndividual}
                className={
                  this.state.individual === true
                    ? classes.HPSBlue
                    : classes.default
                }
              >
                individual
              </Button>
            </ExpansionPanelSummary>
            <ExpansionPanelDetails>
              <Fade in={this.state.individual}>
                <div>
                  {/* <p style={{ color: "#255f90" }}> On or Of Exchange ?</p> */}
                  <Button
                    variant="outlined"
                    onClick={this.changeOnExchange}
                    className={
                      this.state.onExchange === true
                        ? classes.HPSBlue
                        : classes.default
                    }
                  >
                    On Exchange
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={this.changeOffExchange}
                    className={
                      this.state.offExchange === true
                        ? classes.HPSBlue
                        : classes.default
                    }
                  >
                    Off Exchange
                  </Button>
                  <Fade in={this.state.onExchange || this.state.offExchange}>
                    <div>
                      <p style={{ color: "#255f90" }}>
                        Carrier Code for Individual
                      </p>
                      <TextField
                        required={this.state.individual}
                        id="outlined-bare"
                        label=" Required"
                        style={{ width: 770 }}
                        margin="normal"
                        variant="outlined"
                        value={this.state.individualCarrierCode}
                        onChange={this.handleChange("individualCarrierCode")}
                      />
                    </div>
                  </Fade>
                </div>
              </Fade>
            </ExpansionPanelDetails>
          </MuiExpansionPanel>
          <MuiExpansionPanel>
            <ExpansionPanelSummary onClick={this.changeGroup}>
              <Button
                variant="outlined"
                onClick={this.changeGroup}
                className={
                  this.state.group === true ? classes.HPSBlue : classes.default
                }
              >
                Group
              </Button>
            </ExpansionPanelSummary>
            <ExpansionPanelDetails>
              <Fade in={this.state.group}>
                <div>
                  <p style={{ color: "#255f90" }}>Carrier Code for Group</p>
                  <TextField
                    required={this.state.group}
                    id="standard-required"
                    label="Required"
                    margin="normal"
                    variant="outlined"
                    style={{ width: 770 }}
                    value={this.state.groupCarrierCode}
                    onChange={this.handleChange("groupCarrierCode")}
                  />
                </div>
              </Fade>
            </ExpansionPanelDetails>
          </MuiExpansionPanel>
        </Grid>
        <Grid
          container
          alignItems="flex-start"
          justify="flex-end"
          direction="row"
        >
          <Button type="submit" className={classes.nextButton}>
            Next
          </Button>
        </Grid>
        <Button
          className={classes.clearButton}
          variant="outlined"
          color="secondary"
          onClick={this.clear}
        >
          Delete
        </Button>
      </form>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const mapActionToProps = {
  handleUpdateCarrierCode: updateCarrierCode,
  handleUpdateOffExchange: updateOffExchange,
  handleUpdateOnExchange: updateOnExchange,
  handleUpdateGroup: updateGroup
};
const BusinessDetails = connect(
  mapStateToProps,
  mapActionToProps
)(ContainedButtons);
export default withStyles(styles)(BusinessDetails);
