import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Grid from "@material-ui/core/Grid";
import axios from "axios";
import { connect } from "react-redux";
const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center"
    //    alignItems: "center",
    //         height: "100vh"
  },
  HPSBlue: {
    backgroundColor: "#1C80B5",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit * 3,
    width: 175,
    color: "#FFFFF"
  },
  default: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    width: 175,
    margin: theme.spacing.unit * 3,
    color: "#999794"
  },
  nextButton: {
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 125,
    //height: 50,
    fontSize: "14px",
    color: "#ffffff"
  },
  clearButton: {
    margin: theme.spacing.unit * 2,
    width: 175,
    // height: 50,
    fontSize: "14px"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    //textAlign: "center",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  card: {
    //textAlign: "center",
    width: 720
  }
});
class LineOfBusiness extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      health1: false,
      dental1: false,
      vision1: false,
      travel1: false,
      businessType: "Individual",
      subsidyType: "OFF Exchange"
    };
  }
  handleSubmit = e => {
    e.preventDefault();
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      businessType: this.state.businessType,
      subsidyType: this.state.subsidyType,
      health: this.state.health1,
      dental: this.state.dental1,
      vision: this.state.vision1,
      travel: this.state.travel1
    };
    let check = false;
    console.log(JSON.stringify(post));
    fetch(`http://10.24.46.59:8080/carrierlobapi/createLOB `, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(data => console.log(data));

    if (
      this.state.dental1 |
      this.state.health1 |
      (this.state.vision1 === true)
    ) {
      check = true;
    } else check = false;
    // if (this.props.onExchange & (check === true))
    //   this.props.history.push(`/admin/LineOfBusinessOnExchange`);
    // else if (this.props.group & (check === true))
    //   this.props.history.push(`/admin/LineOfBusinessGroup`);
    // else if (check === true) this.props.history.push(`/admin/EdiParent`);
    // else alert("Please select atlease one Line of Business");
  };
  changeHealth1 = () => {
    this.setState({ health1: !this.state.health1 });
  };
  changeDental1 = () => {
    this.setState({ dental1: !this.state.dental1 });
  };
  changeVision1 = () => {
    this.setState({ vision1: !this.state.vision1 });
  };

  clear = () => {
    this.setState({ health1: false });
    this.setState({ dental1: false });
    this.setState({ vision1: false });
    this.setState({ travel1: false });
    axios
      .delete(
        `http://10.24.46.59:8080/carrierlobapi/deleteCarrierLOB/${
          this.props.carrierName
        }/${this.state.subsidyType}`
      )
      .then(res => res.json())
      .then(data => console.log(data));
  };
  render() {
    const { classes } = this.props;
    return (
      <div
        className={classes.root}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "70vh"
        }}
      >
        <Card className={classes.card}>
          <CardHeader
            classes={{ title: classes.label }}
            title="Line of Business available for Induvidual off Exchange"
          />
          <Divider variant="middle" />
          <CardContent>
            <Button
              variant="outlined"
              onClick={this.changeHealth1}
              className={
                this.state.health1 === true ? classes.HPSBlue : classes.default
              }
            >
              Health
            </Button>
            <Button
              variant="outlined"
              onClick={this.changeDental1}
              className={
                this.state.dental1 === true ? classes.HPSBlue : classes.default
              }
            >
              Dental
            </Button>
            <Button
              variant="outlined"
              onClick={this.changeVision1}
              className={
                this.state.vision1 === true ? classes.HPSBlue : classes.default
              }
            >
              Vision
            </Button>
          </CardContent>
          <Grid>
            <CardActions>
              <Button
                className={classes.clearButton}
                variant="outlined"
                color="secondary"
                onClick={this.clear}
              >
                Clear All
              </Button>

              <Grid
                container
                alignItems="flex-start"
                justify="flex-end"
                direction="row"
              >
                <Button
                  className={classes.nextButton}
                  onClick={this.handleSubmit}
                >
                  Next
                </Button>
              </Grid>
            </CardActions>
          </Grid>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName,
  onExchange: state.onExchange,
  offExchange: state.offExchange,
  group: state.group
});
const ReduxLineOfBusiness = connect(mapStateToProps)(LineOfBusiness);
export default withStyles(styles)(ReduxLineOfBusiness);
