class LegalEntity2 extends React.Component {
  state = {
    legalentity: "",
    states: "",
    product: "",
    designer: "",
    panels: []
  };
  constructor(props) {
    super(props);
  }

  addNewlist = () => {
    const { legalentity, states, product, designer, panels } = this.state;
    panels.push({ legalentity, states, product, designer });
    this.setState({ panels });
  };
  handleNext = () => {
    const { panels } = this.state;
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      legalEntityData: panels
    };
