                  id="Designer"
                  select
                  className={classes.textField}
                  label="Choose a Product_Type"
                  value={this.state.designer}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("designer")}
                  SelectProps={{                    
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {designer_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={1}>
                <Fab
                  color="primary"
                  disabled={this.validateSelections()}
                  onClick={this.addNewlist}
                  aria-label="Add"
                  className={classes.fab}
                >
                  <AddIcon />
                </Fab>
              </Grid>
              <Grid item xs={11}>
                {panels.map((event, index) => (
                  <div key={index}>
                    <Paper className={classes.paper}>
                      {`${event.legalentity} in ${event.states} sells ${
                        event.product
                      } and configured as Prod_Type ${event.designer}`}
                      <Fab
                        color="primary"
                        deletevalue={index}
                        onClick={this.deletepanels}
                        size="small"
                        aria-label="Delete"
                        className={classes.deletefab}
                      >
                        <DeleteIcon />
                      </Fab>
                    </Paper>
                  </div>
                ))}
              </Grid>
            </Grid>
          </CardContent>
          <CardActions>
            <Button className={classes.button} onClick={this.handleNext}>
              next
            </Button>
          </CardActions>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const ReduxLegalEntity2 = connect(mapStateToProps)(LegalEntity2);
export default withStyles(styles)(ReduxLegalEntity2);
