import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "../CustomButtons/Button.jsx";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Grid from "@material-ui/core/Grid";
import axios from "axios";
import BusinessDetails from "../BusinessDetails.js";
const styles = theme => ({
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5",
    width: 800
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 800,
    height: 75
  },
  nextButton: {
    backgroundColor: "#1C80B5"
  },
  card: {
    height: 400
  }
});
class CarrierDetails extends React.Component {
  state = {
    carrierName: ""
  };

  constructor(props) {
    super(props);
  }

  handleChange = carrierName => event => {
    this.setState({ [carrierName]: event.target.value });
  };
  handleSubmit = e => {
    e.preventDefault();
    console.log(this.state);
    axios
      .post(`http://10.24.46.14:8080/api/carriers`, this.state)
      .then(function(response) {
        console.log(response);
      });
    this.props.history.push(`/admin/BusinessDetails`);
  };
  render() {
    const { classes } = this.props;

    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "70vh"
        }}
      >
        <Card>
          <CardHeader
            classes={{ title: classes.label }}
            title="Name of the Carrier"
          />
          <Divider variant="middle" />
          <CardContent>
            <TextField
              id="standard-name"
              variant="standard"
              className={classes.textField}
              value={this.state.carrierName}
              onChange={this.handleChange("carrierName")}
              margin="normal"
            />
          </CardContent>
          <Grid
            container
            alignItems="flex-start"
            justify="flex-end"
            direction="row"
          >
            <CardContent>
              <Button
                className={classes.nextButton}
                onClick={this.handleSubmit}
              >
                Next
              </Button>
            </CardContent>
          </Grid>
        </Card>
      </div>
    );
  }
}

export default withStyles(styles)(CarrierDetails);
