import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
const styles = theme => ({
  HPSBlue: {
    backgroundColor: "#1C80B5",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#FFFFF"
  },
  default: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "30px",
    textAlign: "center",
    margin: theme.spacing.unit * 2
  }
});
class LineOfBusiness extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      individual: false
    };
  }
  changeIndividual = () => {
    if (this.state.individual === true) {
      this.setState({ individual: false });
    } else this.setState({ individual: true });
  };
  render() {
    const { classes } = this.props;
    return (
      <Button
        variant="outlined"
        onClick={this.changeIndividual}
        className={
          this.state.individual === true ? classes.HPSBlue : classes.default
        }
      >
        individual
      </Button>
    );
  }
}
export default withStyles(styles)(LineOfBusiness);
