import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import AddressDetails from "./AddressDetails";
import { connect } from "react-redux";
import axios from "axios";
const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  button: {
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 150,
    height: 50,
    fontSize: "14px",
    color: "#ffffff",
    justifyContent: "center"
  },
  LEbutton: {
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 250,
    height: 50,
    fontSize: "14px",
    color: "#ffffff",
    justifyContent: "center"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  card: {
    height: 200,
    width: 800
  },
  addresscard: {
    height: 800,
    width: 800
  },

  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  }
});

class LegalEntity extends React.Component {
  state = {
    nameoflegalentity: "",
    codeoflegalentity: "",
    address: "",
    bank: "",
    checkAddress: false,
    checkBankDetail: false,
    checkLegalEntity: false,
    accountnumber: "",
    bankAccountInfo: []
  };

  constructor(props) {
    super(props);
  }
  componentDidMount() {
    axios
      .get(`http://10.24.46.202:9091/configurator/getBankInfo/${10}`)
      .then(res => {
        const bankAccountInfo = res.data;
        console.log(bankAccountInfo);
        this.setState({ bankAccountInfo });
      });
  }
  validateLegalEntity = () => {
    console.log("Inside validate");
    const { checkAddress, checkBankDetail } = this.state;
    // const temp = !checkAddress || !checkBankDetail;
    // //this.setState({ checkLegalEntity: true });
    return !checkAddress || !checkBankDetail;
  };
  validateNext = () => {
    return !this.state.checkLegalEntity;
  };
  handleaddlegal = e => {
    this.setState({ checkLegalEntity: true });
    e.preventDefault();
    const { panels } = this.state;
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      nameoflegalentity: this.state.nameoflegalentity,
      codeoflegalentity: this.state.codeoflegalentity,
      address: this.state.address,
      bank: this.state.bank
    };
    console.log(JSON.stringify(post));
    fetch(`http://10.24.46.59:8080/legalEntityapi/createLegalEntityPart1`, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(data => console.log(data));
    this.setState({ nameoflegalentity: "" });
    this.setState({ codeoflegalentity: "" });
  };
  handleNext = () => {
    this.props.history.push(`/admin/LegalEntity2`);
    //console.log({ panels });
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  handlebank = e => {
    e.preventDefault();
    this.props.event(this.state);
    this.setState({ accountnumber: "" });
  };
  // handleAddress = () =>{
  //   if(this.state.isaddress)
  //   {
  //   const address=this.state.address;
  //   address.push({id:2});
  //   this.setState(address);
  //   this.setState({isaddress:false});
  // }
  // }
  handleAddress = value => {
    this.setState({ checkAddress: true });
    this.setState({ address: value });
    console.log(this.state.address);
  };
  render() {
    const { classes } = this.props;
    console.log(this.state.Legalentity);
    return (
      <form className={classes.root} onSubmit={this.handleaddlegal}>
        <Grid
          container
          spacing={24}
          direction="column"
          alignItems="center"
          justify="center"
          className={classes.label}
          style={{ minHeight: "100vh" }}
        >
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Name of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.nameoflegalentity}
                  onChange={this.handleChange("nameoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Code of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.codeoflegalentity}
                  onChange={this.handleChange("codeoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.addresscard}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Bank Details"
              />
              <Divider className={classes.divider} />
              <CardContent>
                <Grid container spacing={24} className={classes.label}>
                  <Grid item xs={11}>
                    <TextField
                      select
                      id="Account Number"
                      variant="outlined"
                      label="Account Number"
                      className={classes.textField}
                      fullWidth
                      value={this.state.accountnumber}
                      onChange={this.handleChange("accountnumber")}
                      margin="normal"
                    >
                      {this.state.bankAccountInfo.map((bankAccount, index) => (
                        <MenuItem key={index} value={bankAccount}>
                          {bankAccount}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            {/* {this.state.address.map(card => (
          <AddressDetails status={this.state.isaddress} event={this.handleAddress} key={card.id} />
        ))}    */}
            <AddressDetails onAddressChange={this.handleAddress} />
          </Grid>

          <CardActions>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              disabled={this.validateLegalEntity()}
              className={classes.LEbutton}
            >
              Add Legal Entity
            </Button>
            <Button
              variant="contained"
              color="primary"
              disabled={this.validateNext()}
              className={classes.button}
              onClick={this.handleNext}
            >
              next
            </Button>
          </CardActions>
        </Grid>
      </form>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const ReduxLegalEntity = connect(mapStateToProps)(LegalEntity);
export default withStyles(styles)(ReduxLegalEntity);
