import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import Typography from "@material-ui/core/Typography";
import MenuItem from "@material-ui/core/MenuItem";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import BankDetails from "./BankDetails";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import AddressDetails from "./AddressDetails";

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  button: {
    margin: theme.spacing.unit,
    textTransform: "none",
    fontSize: 18,
    padding: "6px 12px",
    //backgroundColor: "#05EDF8",
    justifyContent: "center"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  card: {
    height: 200,
    width: 800
  },
  addresscard: {
    height: 800,
    width: 800
  },

  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  }
});

class LegalEntity extends React.Component {
  state = {
    nameoflegalentity: "",
    codeoflegalentity: "",
    address: [],
    bank: ""
  };

  constructor(props) {
    super(props);
  }

  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };

  // handleAddress = () =>{
  //   if(this.state.isaddress)
  //   {
  //   const address=this.state.address;
  //   address.push({id:2});
  //   this.setState(address);
  //   this.setState({isaddress:false});
  // }
  // }
  handleAddress = value => {
    const { address } = this.state;
    address.push({ value });
    this.setState({ address });
    // console.log(this.state.address);
  };
  handlebank = value => {
    this.setState({ bank: value });
  };
  render() {
    const { classes } = this.props;
    console.log(this.state.Legalentity);
    return (
      <div className={classes.root}>
        <Grid
          container
          spacing={24}
          direction="column"
          alignItems="center"
          justify="center"
          className={classes.label}
          style={{ minHeight: "100vh" }}
        >
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Name of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.nameoflegalentity}
                  onChange={this.handleChange("nameoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            <Card className={classes.card}>
              <CardHeader
                classes={{ title: classes.label }}
                title="Code of Legal Entity"
              />
              <Divider variant="middle" />
              <CardContent>
                <TextField
                  id="standard-name"
                  variant="standard"
                  fullWidth
                  value={this.state.codeoflegalentity}
                  onChange={this.handleChange("codeoflegalentity")}
                  margin="normal"
                />
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12}>
            {/* {this.state.address.map(card => (
          <AddressDetails status={this.state.isaddress} event={this.handleAddress} key={card.id} />
        ))}    */}
            <AddressDetails onAddressChange={this.handleAddress} />
          </Grid>
          <Grid item xs={12}>
            <BankDetails event={this.handlebank} />
          </Grid>
          <CardActions className={classes.button}>
            <Button
              variant="contained"
              color="primary"
              onClick={this.handleNext}
            >
              next
            </Button>
          </CardActions>
        </Grid>
      </div>
    );
  }
}
export default withStyles(styles)(LegalEntity);
