import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import FormLabel from "@material-ui/core/FormLabel";
import Fade from "@material-ui/core/Fade";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import MuiExpansionPanel from "@material-ui/core/ExpansionPanel";
import MuiExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import MuiExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";

const ExpansionPanelSummary = withStyles({
  root: {
    backgroundColor: "rgba(0,0,0,.03)",
    borderBottom: "1px solid rgba(0,0,0,.125)"
  }
})(props => <MuiExpansionPanelSummary {...props} />);

ExpansionPanelSummary.muiName = "ExpansionPanelSummary";

const ExpansionPanelDetails = withStyles(theme => ({
  root: {
    padding: theme.spacing.unit
  }
}))(MuiExpansionPanelDetails);

const styles = theme => ({
  HPSBlue: {
    backgroundColor: "#1C80B5",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#FFFFF"
  },
  default: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "30px",
    textAlign: "center",
    margin: theme.spacing.unit * 2
  }
});

class ContainedButtons extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      individual: false,
      onExchange: false,
      offExchange: false,
      group: false,
      expanded: "panel1"
    };
  }
  handleChange = panel => (event, expanded) => {
    this.setState({
      expanded: expanded ? panel : false
    });
  };
  changeIndividual = () => {
    if (this.state.individual === true) {
      this.setState({ individual: false });
      this.setState({ onExchange: false });
      this.setState({ offExchange: false });
    } else this.setState({ individual: true });
  };
  changeOnExchange = () => {
    if (this.state.onExchange === true) this.setState({ onExchange: false });
    else {
      this.setState({ onExchange: true });
    }
  };
  changeOffExchange = () => {
    if (this.state.offExchange === true) this.setState({ offExchange: false });
    else {
      this.setState({ offExchange: true });
    }
  };
  changeGroup = () => {
    if (this.state.group === true) this.setState({ group: false });
    else {
      this.setState({ group: true });
    }
  };

  render() {
    const { classes } = this.props;
    return (
      <div>
        <FormLabel className={classes.label} component="legend">
          Please Choose your Line of Business
        </FormLabel>
        <Grid
          container
          spacing={2}
          direction="column"
          alignItems="center"
          justify="center"
          style={{ minHeight: "90vh" }}
        >
          <MuiExpansionPanel>
            <ExpansionPanelSummary onClick={this.changeIndividual}>
              <Button
                variant="outlined"
                onClick={this.changeIndividual}
                className={
                  this.state.individual === true
                    ? classes.HPSBlue
                    : classes.default
                }
              >
                individual
              </Button>
            </ExpansionPanelSummary>
            <ExpansionPanelDetails>
              <Fade in={this.state.individual}>
                <div>
                  <p style={{ color: "#255f90" }}> On or Of Exchange ?</p>
                  <Button
                    variant="outlined"
                    onClick={this.changeOnExchange}
                    className={
                      this.state.onExchange === true
                        ? classes.HPSBlue
                        : classes.default
                    }
                  >
                    On Exchange
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={this.changeOffExchange}
                    className={
                      this.state.offExchange === true
                        ? classes.HPSBlue
                        : classes.default
                    }
                  >
                    Off Exchange
                  </Button>
                  <Fade in={this.state.onExchange || this.state.offExchange}>
                    <div>
                      <p style={{ color: "#255f90" }}>
                        Carrier Code for Individual
                      </p>
                      <TextField
                        id="outlined-bare"
                        style={{ width: 770 }}
                        margin="normal"
                        variant="outlined"
                      />
                    </div>
                  </Fade>
                </div>
              </Fade>
            </ExpansionPanelDetails>
          </MuiExpansionPanel>
          <MuiExpansionPanel>
            <ExpansionPanelSummary onClick={this.changeGroup}>
              <Button
                variant="outlined"
                onClick={this.changeGroup}
                className={
                  this.state.group === true ? classes.HPSBlue : classes.default
                }
              >
                Group
              </Button>
            </ExpansionPanelSummary>
            <ExpansionPanelDetails>
              <Fade in={this.state.group}>
                <div>
                  <p style={{ color: "#255f90" }}>Carrier Code for Group</p>
                  <TextField
                    id="outlined-bare"
                    margin="normal"
                    variant="outlined"
                    style={{ width: 770 }}
                  />
                </div>
              </Fade>
            </ExpansionPanelDetails>
          </MuiExpansionPanel>
        </Grid>
      </div>
    );
  }
}
export default withStyles(styles)(ContainedButtons);
