import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import DeleteIcon from "@material-ui/icons/Delete";
import MenuItem from "@material-ui/core/MenuItem";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Fab from "@material-ui/core/Fab";

import AddIcon from "@material-ui/icons/Add";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center"
    //    alignItems: "center",
    //         height: "100vh"
  },
  paper: {
    textAlign: "center",
    padding: theme.spacing.unit * 2.5,
    border: 2,
    borderColor: "#090909"
  },
  button: {
    position: "absolute",
    bottom: "2rem",
    right: "2rem",
    justifyContent: "center"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  fab: {
    right: 1,
    marginLeft: "30px",
    marginTop: "8px"
  },
  deletefab: {
    position: "absolute",
    marginLeft: "180px",
    marginTop: "-8px"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  paper: {
    textAlign: "center",
    padding: theme.spacing.unit * 2.5,
    border: 2,
    borderColor: "#090909",
    marginBottom: "15px"
  },
  addresscard: {
    height: 800,
    width: 1200
  },
  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  },
  TextInputStyleClass: {
    textAlign: "centre",
    borderWidth: 2,
    borderColor: "#090909",
    height: 50
  }
});
const states_option = [
  {
    value: "TX",
    label: "TX"
  },
  {
    value: "NM",
    label: "NM"
  },
  {
    value: "LA",
    label: "LA"
  }
];
class LegalEntity2 extends React.Component {
  state = {
    states: "",
    tpaId: "",
    panels: []
  };
  constructor(props) {
    super(props);
  }

  addNewlist = () => {
    const { states, tpaId, panels } = this.state;
    panels.push({ states, tpaId });
    this.setState({ panels });
  };
  printlist = () => {
    const { panels } = this.state;
    console.log({ panels });
  };
  validateSelections = () => {
    const { states, tpaId } = this.state;
    return !states || !tpaId;
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  deletepanels = event => {
    const { panels } = this.state;
    var index = event.currentTarget.attributes.deletevalue.nodeValue;
    console.log(event.currentTarget.attributes.deletevalue.nodeValue);
    panels.splice(index, 1);
    this.setState({ panels });
  };
  render() {
    console.log(this.state.panels);
    const { classes } = this.props;
    const { states, tpaId, panels } = this.state;
    return (
      <div className={classes.root}>
        <Card className={classes.addresscard}>
          <CardHeader
            classes={{ title: classes.label }}
            title="Configure States with TPA Id"
          />
          <Divider className={classes.divider} />
          <CardContent>
            <Grid container spacing={24} className={classes.label}>
              <Grid item xs={5}>
                <TextField
                  id="State_Value"
                  select
                  className={classes.textField}
                  label="Choose a State"
                  value={this.state.states}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("states")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {states_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={5}>
                <TextField
                  id="tpaId"
                  //select
                  className={classes.textField}
                  label="Choose a tpaId"
                  value={this.state.tpaId}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("tpaId")}
                  // SelectProps={{
                  //   MenuProps: {
                  //     className: classes.menu
                  //   }
                  // }}
                >
                  {/* {designer_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))} */}
                </TextField>
              </Grid>

              <Grid item xs={1}>
                <Fab
                  color="primary"
                  disabled={this.validateSelections()}
                  onClick={this.addNewlist}
                  aria-label="Add"
                  className={classes.fab}
                >
                  <AddIcon />
                </Fab>
              </Grid>
              <Grid item xs={11}>
                {panels.map((event, index) => (
                  <div key={index}>
                    <Paper className={classes.paper}>
                      {`${event.states} TPA id is ${event.tpaId}`}
                      <Fab
                        color="primary"
                        deletevalue={index}
                        onClick={this.deletepanels}
                        size="small"
                        aria-label="Delete"
                        className={classes.deletefab}
                      >
                        <DeleteIcon />
                      </Fab>
                    </Paper>
                  </div>
                ))}
              </Grid>
            </Grid>
          </CardContent>
          <CardActions className={classes.button}>
            <Button
              variant="contained"
              color="primary"
              onClick={this.printlist}
            >
              next
            </Button>
          </CardActions>
        </Card>
      </div>
    );
  }
}

export default withStyles(styles)(LegalEntity2);
