import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import DeleteIcon from "@material-ui/icons/Delete";
import MenuItem from "@material-ui/core/MenuItem";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Fab from "@material-ui/core/Fab";

import AddIcon from "@material-ui/icons/Add";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center"
    //    alignItems: "center",
    //         height: "100vh"
  },
  paper: {
    textAlign: "center",
    padding: theme.spacing.unit * 2.5,
    border: 2,
    borderColor: "#090909"
  },
  button: {
    position: "absolute",
    bottom: "2rem",
    right: "2rem",
    justifyContent: "center"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  fab: {
    right: 1,
    marginLeft: "30px",
    marginTop: "8px"
  },
  deletefab: {
    position: "absolute",
    marginLeft: "180px",
    marginTop: "-8px"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  paper: {
    textAlign: "center",
    padding: theme.spacing.unit * 2.5,
    border: 2,
    borderColor: "#090909",
    marginBottom: "15px"
  },
  addresscard: {
    height: 800,
    width: 1200
  },
  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  },
  TextInputStyleClass: {
    textAlign: "centre",
    borderWidth: 2,
    borderColor: "#090909",
    height: 50
  }
});
const legal_entity_option = [
  {
    value: "LG001",
    label: "LG001"
  },
  {
    value: "LG002",
    label: "LG002"
  },
  {
    value: "LG003",
    label: "LG003"
  }
];
const states_option = [
  {
    value: "TX",
    label: "TX"
  },
  {
    value: "NM",
    label: "NM"
  },
  {
    value: "LA",
    label: "LA"
  }
];
const product_option = [
  {
    value: "HEALTH",
    label: "HEALTH"
  },
  {
    value: "DENTAL",
    label: "DENTAL"
  },
  {
    value: "VISION",
    label: "VISION"
  }
];
const designer_option = [
  {
    value: "A",
    label: "A"
  },
  {
    value: "B",
    label: "B"
  },
  {
    value: "C",
    label: "C"
  }
];
class LegalEntity2 extends React.Component {
  state = {
    legalentity: "",
    states: "",
    product: "",
    designer: "",
    panels: []
  };
  constructor(props) {
    super(props);
  }

  addNewlist = () => {
    const { legalentity, states, product, designer, panels } = this.state;
    panels.push({ legalentity, states, product, designer });
    this.setState({ panels });
  };
  handleNext = () => {
    const { panels } = this.state;
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      ediData: panels
    };
    console.log(JSON.stringify(post));
    axios
      .post(`http://10.24.46.14:8080/api/allCarriers`, {
        body: JSON.stringify(post)
      })
      .then(function(response) {
        console.log(response);
      });
    this.props.history.push(``);
    //console.log({ panels });
  };
  validateSelections = () => {
    const { legalentity, states, product, designer } = this.state;
    return !legalentity || !states || !product || !designer;
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  deletepanels = event => {
    const { panels } = this.state;
    var index = event.currentTarget.attributes.deletevalue.nodeValue;
    console.log(event.currentTarget.attributes.deletevalue.nodeValue);
    panels.splice(index, 1);
    this.setState({ panels });
  };
  render() {
    const { classes } = this.props;
    const { legalentity, states, product, designer, panels } = this.state;
    return (
      <div className={classes.root}>
        <Card className={classes.addresscard}>
          <CardHeader
            classes={{ title: classes.label }}
            title="Configure Legal Entity and Prod Type"
          />
          <Divider className={classes.divider} />
          <CardContent>
            <Grid container spacing={24} className={classes.label}>
              <Grid item xs={3}>
                <TextField
                  id="Legal_Entity"
                  select
                  className={classes.textField}
                  label="Choose a Legal Entity"
                  value={this.state.legalentity}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("legalentity")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {legal_entity_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="State_Value"
                  select
                  className={classes.textField}
                  label="Choose a State"
                  value={this.state.states}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("states")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {states_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={3}>
                <TextField
                  id="Product"
                  select
                  className={classes.textField}
                  label="Choose a Product"
                  value={this.state.product}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("product")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {product_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="Designer"
                  select
                  className={classes.textField}
                  label="Choose a Designer"
                  value={this.state.designer}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("designer")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {designer_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={1}>
                <Fab
                  color="primary"
                  disabled={this.validateSelections()}
                  onClick={this.addNewlist}
                  aria-label="Add"
                  className={classes.fab}
                >
                  <AddIcon />
                </Fab>
              </Grid>
              <Grid item xs={11}>
                {panels.map((event, index) => (
                  <div key={index}>
                    <Paper className={classes.paper}>
                      {`${event.legalentity} in ${event.states} sells ${
                        event.product
                      } and configured as Prod_Type ${event.designer}`}
                      <Fab
                        color="primary"
                        deletevalue={index}
                        onClick={this.deletepanels}
                        size="small"
                        aria-label="Delete"
                        className={classes.deletefab}
                      >
                        <DeleteIcon />
                      </Fab>
                    </Paper>
                  </div>
                ))}
              </Grid>
            </Grid>
          </CardContent>
          <CardActions className={classes.button}>
            <Button
              variant="contained"
              color="primary"
              onClick={this.handleNext}
            >
              next
            </Button>
          </CardActions>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const ReduxLegalEntity2 = connect(mapStateToProps)(LegalEntity2);
export default withStyles(styles)(ReduxLegalEntity2);
