import React from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import DeleteIcon from "@material-ui/icons/Delete";
import MenuItem from "@material-ui/core/MenuItem";
import CardContent from "@material-ui/core/CardContent";
import { CardActions, CardHeader } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Fab from "@material-ui/core/Fab";
import { connect } from "react-redux";
import AddIcon from "@material-ui/icons/Add";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import axios from "axios";
const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    justifyContent: "center"
    //    alignItems: "center",
    //         height: "100vh"
  },

  paper: {
    textAlign: "center",
    padding: theme.spacing.unit * 2.5,
    border: 2,
    borderColor: "#090909"
  },
  button: {
    position: "absolute",
    bottom: "2rem",
    right: "2rem",
    justifyContent: "center",
    margin: theme.spacing.unit * 2,
    backgroundColor: "#1C80B5",
    width: 125,
    //height: 50,
    fontSize: "14px",
    color: "#ffffff"
  },
  label: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "20px",
    margin: theme.spacing.unit * 2,
    color: "#1C80B5"
  },
  fab: {
    right: 1,
    marginLeft: "30px",
    marginTop: "8px"
  },
  deletefab: {
    position: "absolute",
    marginLeft: "180px",
    marginTop: "-8px"
  },
  textField: {
    backgroundColor: "#F0F0F0",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    fontSize: "18px",
    margin: theme.spacing.unit,
    color: "#999794"
  },
  paper: {
    textAlign: "center",
    padding: theme.spacing.unit * 2.5,
    border: 2,
    borderColor: "#090909",
    marginBottom: "15px"
  },
  addresscard: {
    height: 800,
    width: 1200
  },
  divider: {
    margin: `${theme.spacing.unit * 2}px 0`
  },
  TextInputStyleClass: {
    textAlign: "centre",
    borderWidth: 2,
    borderColor: "#090909",
    height: 50
  }
});
const state_option = [
  {
    value: "TX",
    label: "TX"
  },
  {
    value: "NM",
    label: "NM"
  },
  {
    value: "LA",
    label: "LA"
  }
];
class Edi extends React.Component {
  state = {
    state: "",
    tpaId: "",
    hiosId: "",
    payeeId: "",
    senderId: "",
    receiverId: "",
    panels: []
  };
  constructor(props) {
    super(props);
  }

  addNewlist = () => {
    const {
      state,
      tpaId,
      hiosId,
      payeeId,
      senderId,
      receiverId,
      panels
    } = this.state;
    panels.push({ state, tpaId, hiosId, payeeId, senderId, receiverId });
    this.setState({ panels });
  };
  handleNext = () => {
    const { panels } = this.state;
    const post = {
      carrierName: this.props.carrierName,
      carrierCode: this.props.carrierCode,
      ediData: panels
    };
    console.log(JSON.stringify(post));
    fetch(`http://10.24.46.158:8080/ediapi/createEdi`, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(data => console.log(data));
    this.props.history.push(`/admin/LegalEntity`);
  };
  validateSelections = () => {
    const { state, tpaId, hiosId, payeeId, senderId, receiverId } = this.state;
    return !state || !tpaId;
  };
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  deletepanels = event => {
    const { panels } = this.state;
    var index = event.currentTarget.attributes.deletevalue.nodeValue;
    console.log(event.currentTarget.attributes.deletevalue.nodeValue);
    panels.splice(index, 1);
    this.setState({ panels });
  };
  render() {
    console.log(this.state.panels);
    const { classes } = this.props;
    const {
      state,
      tpaId,
      hiosId,
      payeeId,
      senderId,
      receiverId,
      panels
    } = this.state;
    return (
      <div className={classes.root}>
        <Card className={classes.addresscard}>
          <CardHeader
            classes={{ title: classes.label }}
            title="EDI Configure for States"
          />
          <Divider className={classes.divider} />
          <CardContent>
            <Grid container spacing={24} className={classes.label}>
              <Grid item xs={2}>
                <TextField
                  id="State_Value"
                  select
                  className={classes.textField}
                  label="Choose a State"
                  value={this.state.state}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("state")}
                  SelectProps={{
                    MenuProps: {
                      className: classes.menu
                    }
                  }}
                >
                  {state_option.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={1}>
                <TextField
                  id="tpaId"
                  className={classes.textField}
                  label="tpaId"
                  value={this.state.tpaId}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("tpaId")}
                />
              </Grid>
              <Grid item xs={1}>
                <TextField
                  id="hiosId"
                  className={classes.textField}
                  label="hiosId"
                  value={this.state.hiosId}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("hiosId")}
                />
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="payeeId"
                  className={classes.textField}
                  label="payeeId"
                  value={this.state.payeeId}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("payeeId")}
                />
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="senderId"
                  className={classes.textField}
                  label="senderId"
                  value={this.state.senderId}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("senderId")}
                />
              </Grid>
              <Grid item xs={2}>
                <TextField
                  id="receiverId"
                  className={classes.textField}
                  label="receiverId"
                  value={this.state.receiverId}
                  variant="outlined"
                  fullWidth
                  onChange={this.handleChange("receiverId")}
                />
              </Grid>
              <Grid item xs={1}>
                <Fab
                  color="primary"
                  disabled={this.validateSelections()}
                  onClick={this.addNewlist}
                  aria-label="Add"
                  className={classes.fab}
                >
                  <AddIcon />
                </Fab>
              </Grid>
              <Grid item xs={11}>
                {panels.map((event, index) => (
                  <div key={index}>
                    <Paper className={classes.paper}>
                      {`${event.state}'s TPA ID is ${event.tpaId}, HIOS ID is ${
                        event.hiosId
                      }, PAYEE ID ${event.payeeId} SENDER ID is ${
                        event.senderId
                      }, RECEIVER ID is ${event.receiverId}`}
                      <Fab
                        color="primary"
                        deletevalue={index}
                        onClick={this.deletepanels}
                        size="small"
                        aria-label="Delete"
                        className={classes.deletefab}
                      >
                        <DeleteIcon />
                      </Fab>
                    </Paper>
                  </div>
                ))}
              </Grid>
            </Grid>
          </CardContent>
          <CardActions>
            <Button className={classes.button} onClick={this.handleNext}>
              next
            </Button>
          </CardActions>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  carrierCode: state.carrierCode,
  carrierName: state.carrierName
});
const ReduxEdi = connect(mapStateToProps)(Edi);
export default withStyles(styles)(ReduxEdi);
